mapper.select:
SELECT EMAIL, NAME, ADDRESS, BIRTH, PHONE
FROM USER
WHERE EMAIL = #{email}

mapper.update:
UPDATE USER
SET EMAIL = #{email},  NAME = #{name}, ADDRESS = #{address}, BIRTH = #{birth}, PHONE = #{phone}
WHERE EMAIL = #{email}

